func.src:

$sku = $rec['商品番号'];
$sku_token = explode('-', $sku);
$ceil = ((int)$sku_token[1] - 1) / 500;
$ceil = ((int)$ceil + 1) * 500;

$links = array();
for($c = 1; $c <= 3; $c++){
  $links[] = sprintf(
    "http://image.rakuten.co.jp/titosoy/cabinet/items%s/%s-%s.jpg",
    $ceil, strtolower($sku), $c
  );
}
return implode(" ", $links);
